import React, { useState } from 'react'
const Course = () => {
    const [items, setItems] = useState([]);
    const [text, setText] = useState('');
  
    const addItem = () => {
        if (text.trim() !== '') {
        setItems([...items, text]);
        setText('');
        }
      };
   return (
      <div>
        <h1>Todo List</h1>
        <select
          type="text"
         
          onChange={(e) => setText(e.target.value)}
        >   <option>{text}</option>
            <option>html</option>
            <option>css</option>
            <option>react</option>
            </select>
        <button onClick={addItem}>Add Item</button>
        <ul>
        {items.map((item,i) => (
            <li key={i}> {item}</li>
               ))}
        </ul>
       
         
        
         </div>
    );
}

export default Course
